mapper.select:
select board_id, member_id, writer, title, content, regdate, updatedate, deletedate
from board
where member_id = #{memberId}

select board_id, member_id, writer, title, content, regdate, updatedate, deletedate
from board
where board_id = #{boardId}

select board_id, member_id, writer, title, content, regdate, updatedate, deletedate
from board
order by regdate desc limit #{startPosition}, #{maxResult}
